update_index_datetime <- function(index,
                                  slug,
                                  update = "datetime_updated",
                                  use_data = FALSE,
                                  overwrite = FALSE) {
  index[index$slug == slug, as.name(update)] <- Sys.time()

  if (use_data) {
    usethis::use_data(index, overwrite = overwrite)
  }

  return(index)
}

update_index_datetime(index = data_index, slug = "adopted_plans")
